import { EventEmitter, Injectable, Output } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { Product } from '../shared/models/product';
import { ShopItem } from '../shared/models/shopItem';

@Injectable()
export class ShoppingCartService {
    counter = new BehaviorSubject<number>(0);

    shopItems: ShopItem[] = [];

    constructor(){
        console.log(this.counter)
    }

    public get(): ShopItem[] {
        return this.shopItems;
    }

    public add(item: ShopItem): void {
        let i= this.shopItems.findIndex(item => item.id === item.id);
        if(i > 0){
            this.shopItems[i].count++;
        }else{
            this.shopItems.push(item);
        }
        debugger;
        this.counter.next(this.shopItems.length);
    
    }

    public remove(id: number): void{
        let i= this.shopItems.findIndex(item => item.id === id);
        i > 0 ? this.shopItems.splice(i, 1) : "";
        this.counter.next(this.shopItems.length);

    }

}
