import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { ShopItem } from '../shared/models/shopItem';

@Injectable()
export class ShoppingCartService {

    shopItems: ShopItem[];

    constructor(){
        this.shopItems= [
            {
                id: 0,
                title: "Notebook",
                description: "Notebook ASUS X515EA Intel I3 1115 G4 | RAM 4GB | SSD 256Gb | 15.6 | X515EA-BR1324",
                img: "https://s3-sa-east-1.amazonaws.com/saasargentina/JyNMe54QqkoWP7UGGiz6/imagen"
            },
        ];
    }

    public getItems(): ShopItem[] {
        return this.shopItems;
    }

    public addItem(item: ShopItem): void {
        this.shopItems.push(item);
    }

    public removeItem(id: number): void{
        let i= this.shopItems.findIndex(item => item.id === id);
        i > 0 ? this.shopItems.splice(i, 1) : "";
    }

}
